// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

contract PredictionMarket {
    address public owner;
    mapping(address => bool) public registeredAggregators;

    event AggregatorRegistered(address indexed aggregator);
    event PredictionPlaced(
        address indexed user,
        address indexed aggregator,
        uint256 amount
    );

    constructor() {
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner can call this");
        _;
    }

    function registerAggregator(address aggregator) external onlyOwner {
        require(aggregator != address(0), "Invalid aggregator address");
        require(!registeredAggregators[aggregator], "Already registered");

        registeredAggregators[aggregator] = true;
        emit AggregatorRegistered(aggregator);
    }

    function placePrediction(
        address aggregator,
        uint8 predictedOutcome
    ) external payable {
        require(registeredAggregators[aggregator], "Aggregator not registered");
        require(msg.value > 0, "Must send BNB with prediction");

        emit PredictionPlaced(msg.sender, aggregator, msg.value);

        // Forward bet to aggregator
        (bool success, ) = aggregator.call{value: msg.value}(
            abi.encodeWithSignature("placeBet(uint8)", predictedOutcome)
        );
        require(success, "Bet placement failed");
    }

    function isAggregatorRegistered(
        address aggregator
    ) external view returns (bool) {
        return registeredAggregators[aggregator];
    }
}

